// src/OGImageGenerator.js
import { toPng } from 'html-to-image';
import { saveAs } from 'file-saver';

export const generateOgImage = (node) => {
  toPng(node)
    .then((dataUrl) => {
      saveAs(dataUrl, 'og-image.png');
    })
    .catch((error) => {
      console.error('Error generating image:', error);
    });
};
